<script setup>
import { storeToRefs } from 'pinia';
import { useThemeStore } from '../stores/darkmode.js'
import { useLanguageStore } from '../stores/langStore'
import { userStore } from '../stores/UserStore'
import { local_storage_get } from '../utils';
import { useI18n } from 'vue-i18n'
import axios from 'axios'
import { initFlowbite } from 'flowbite'
import { onBeforeMount, onMounted, ref } from 'vue';
import { RouterLink } from 'vue-router';
import { acl } from '@/router/acl'

// initialize components based on data attribute selectors
  onMounted(() => {
      initFlowbite();
  })
const themeStore = useThemeStore()
const langStore = useLanguageStore()
const usStore = userStore()

const { isDark } = storeToRefs(themeStore)
const { actual } = storeToRefs(langStore)
const { t, locale } = useI18n()

const user = ref(null)

function changeLanguage (lang) {
  langStore.changeLanguage(lang)
  locale.value = lang

  // change in database
  axios.put('admin/users/' + usStore.user.id, {
    default_language: lang
  })
  .then(response => {
    window.location.href = window.location.href
  })
  .catch(err => {
    console.log('Some error occured')
  })

}

onBeforeMount(async () => {
  user.value = await acl()
})

onMounted(async () => {
  initFlowbite()

  const btnToggle = document.getElementById("btn-toggle-sidebar")
  const sidebar = document.getElementById("logo-sidebar")
  const overlay = document.getElementById("app-overlay")

  btnToggle.addEventListener("click", function (e) {

    sidebar.classList.toggle("-translate-x-full")
    overlay.classList.toggle("hidden")
  })

  overlay.addEventListener("click", function() {
    sidebar.classList.toggle("-translate-x-full")
    overlay.classList.toggle("hidden")
  })
})


// logout
const handleLogout = event => {
  let cookie_name = local_storage_get('cookie_name')
  let cookie = cookie_name + '=;Max-Age=0'
  document.cookie = cookie
  window.location.href = '/'
}
</script>

